var video = document.getElementById("videoPlay");
if(video){
    video.onclick=function(){
        if(video.paused){
            video.play();
            $(".video_default").hide();
        }else{
            video.pause();
            $(".video_default").show();
        }
    }
    video.addEventListener("ended",function(){
        video.currentTime = 0;
        $(".video_default").show();
    })
}
function get_collection(){
